import { Avatar, Button, Container, TextField, Typography } from '@material-ui/core';
import React, { useState } from 'react';
import style from '../Tool/Style';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import { loginUsuario } from '../../actions/UsuarioAction';
import { withRouter } from 'react-router-dom';
import { useStateValue } from '../../contexto/store';

const Login = (props) => {
    const [{ usuarioSesion }, dispatch] = useStateValue();
    const [usuario, setUsuario] = useState({
        Email: '',
        Password: ''
    })

    const ingresarValoresMemoria = e => {
        const {name, value} = e.target;
        setUsuario(anterior => ({
            ...anterior,
            [name]: value
        }))
    }

    const loginUsuarioClick = e => {
        e.preventDefault();
        loginUsuario(usuario, dispatch).then(response => {
           
          if(response.status == 200){
            window.localStorage.setItem('token_seguridad', response.data.token);
            props.history.push("/");
          }else {
            dispatch({
              type : "OPEN_SNACKBAR",
              openMensaje: {
                open: true,
                mensaje: "Las credenciales del usuario son incorrectas"
              }
            })
          }


        })
    }


    return (
      <Container maxWidth="xs">
        <div style={style.paper}>
          <Avatar style={style.avatar}>
            <LockOpenIcon style={style.icon} />
          </Avatar>

          <Typography component="h1" variant="h5">
            Login de Usuario
          </Typography>

          <form style={style.form}>
            <TextField
              name="Email" value={usuario.Email} onChange={ingresarValoresMemoria}
              variant="outlined"
              label="Ingrese username"
              fullWidth
              margin="normal"
            />
            <TextField
              name="Password" value={usuario.Password} onChange={ingresarValoresMemoria}
              type="Password"
              variant="outlined"
              label="password"
              fullWidth
              margin="normal"
            />

            <Button
              type="submit" onClick={loginUsuarioClick}
              fullWidth
              variant="contained"
              color="primary"
              style={style.submit}
             >
              Enviar
            </Button>
          </form>
        </div>
      </Container>
    );
};

export default withRouter(Login);
